      // Simulate fluctuations
      const newTemp = 20 + Math.random() * 4
      const newHumidity = 40 + Math.random() * 15
      
      setTemp(newTemp)
      setHumidity(newHumidity)
      
      setTempTrend(prev => [...prev.slice(1), newTemp])
      setHumidityTrend(prev => [...prev.slice(1), newHumidity])
