            {/* 90% and 99% recovery lines */}
            <line x1="50" y1={260 - (90 * 2.4)} x2="580" y2={260 - (90 * 2.4)} stroke="#22c55e" strokeWidth="2" strokeDasharray="6" opacity="0.5" />
